DROP TABLE IF EXISTS PRODUCTS;
DROP TABLE IF EXISTS SHOPPING_LISTS;
DROP TABLE IF EXISTS SHOPPING_LIST_PRODUCT_MAPPINGS;
CREATE TABLE PRODUCTS ( id integer primary key, name String, barcode_id String, category String, price Numeric, uom String, status text);
CREATE TABLE SHOPPING_LISTS ( id integer primary key, name String , cost Numeric);
CREATE TABLE SHOPPING_LIST_PRODUCT_MAPPINGS (shopping_list_id integer, product_id integer,quantity integer, PRIMARY KEY (shopping_list_id, product_id));

