DROP TABLE IF EXISTS PRODUCTS;
DROP TABLE IF EXISTS SHOPPING_LISTS;
CREATE TABLE PRODUCTS ( id integer primary key, name String, barcode_id String, category String, cost Numeric, uom String, status text, x_pos String, y_pos String );
CREATE TABLE SHOPPING_LISTS ( id integer primary key, name String );
CREATE TABLE SHOPPING_LIST_PRODUCT_MAPPINGS (shopping_list_id integer, product_id integer, PRIMARY KEY (shopping_list_id, product_id));

